import React, { useState } from 'react';
import { 
  Container, 
  Typography, 
  Box, 
  Button, 
  TextField, 
  Paper,
  IconButton
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // TODO: Implement actual logout logic
    localStorage.removeItem('user');
    navigate('/login');
  };

  const [thought, setThought] = useState('');
  const [thoughts, setThoughts] = useState([]);

  const handleShareThought = (e) => {
    e.preventDefault();
    if (thought.trim()) {
      setThoughts([...thoughts, { id: Date.now(), text: thought }]);
      setThought('');
    }
  };

  return (
    <Container maxWidth="md" sx={{ height: '100vh', display: 'flex', flexDirection: 'column' }}>
      <Box sx={{ mt: 4, mb: 4, flex: 1, overflow: 'auto' }}>
        <Typography variant="h3" component="h1" gutterBottom align="center">
          Welcome to Thoughts
        </Typography>
        <Typography variant="h5" color="text.secondary" paragraph align="center">
          Your personal space to share and explore thoughts
        </Typography>

        <Box sx={{ mt: 4, mb: 2, display: 'flex', justifyContent: 'flex-end' }}>
          <Button variant="outlined" onClick={handleLogout}>
            Logout
          </Button>
        </Box>

        {/* Thoughts List */}
        <Box sx={{ mt: 4, mb: 4 }}>
          {thoughts.map((item) => (
            <Paper 
              key={item.id} 
              elevation={2} 
              sx={{ 
                p: 2, 
                mb: 2, 
                borderRadius: 2,
                bgcolor: 'background.paper'
              }}
            >
              <Typography>{item.text}</Typography>
            </Paper>
          ))}
        </Box>
      </Box>

      {/* Fixed bottom input */}
      <Box 
        component="form" 
        onSubmit={handleShareThought}
        sx={{ 
          position: 'sticky', 
          bottom: 0, 
          py: 2, 
          bgcolor: 'background.default',
          borderTop: '1px solid',
          borderColor: 'divider',
          backdropFilter: 'blur(10px)'
        }}
      >
        <Box sx={{ display: 'flex', gap: 1 }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Share your thoughts..."
            value={thought}
            onChange={(e) => setThought(e.target.value)}
            sx={{ 
              '& .MuiOutlinedInput-root': {
                borderRadius: '50px',
                backgroundColor: 'background.paper',
              }
            }}
            InputProps={{
              endAdornment: (
                <IconButton 
                  type="submit" 
                  color="primary"
                  disabled={!thought.trim()}
                >
                  <SendIcon />
                </IconButton>
              ),
            }}
          />
        </Box>
      </Box>
    </Container>
  );
};

export default Home;
