const API_BASE_URL = 'http://localhost:9000/api';

// Helper function to handle API requests
const apiRequest = async (endpoint, options = {}) => {
  const token = localStorage.getItem('token');

  const headers = {
    'Content-Type': 'application/json',
    ...options.headers,
  };

  if (token) {
    headers['Authorization'] = `Bearer ${token}`;
  }

  try {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      ...options,
      headers,
      body: options.body ? JSON.stringify(options.body) : undefined,
    });

    const data = await response.json();

    if (!response.ok) {
      throw new Error(data.error || 'Something went wrong');
    }

    return data;
  } catch (error) {
    console.error('API Error:', error);
    throw error;
  }
};

// Auth API
export const authAPI = {
  login: async (email, password) => {
    const response = await apiRequest('/auth/login', {
      method: 'POST',
      body: { email, password },
    });
    // Include email in the response for the frontend
    return {
      ...response,
      user: { email }
    };
  },

  register: async (email, password) => {
    const response = await apiRequest('/auth/register', {
      method: 'POST',
      body: { email, password },
    });
    // Include email in the response for the frontend
    return {
      ...response,
      user: { email }
    };
  },

  getCurrentUser: () => {
    const user = localStorage.getItem('user');
    return user ? JSON.parse(user) : null;
  },

  logout: () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
  },
};

// Thoughts API
export const thoughtsAPI = {
  getThoughts: async () => {
    const response = await apiRequest('/thoughts');
    // The backend returns thoughts in the data field
    return Array.isArray(response) ? response : [];
  },

  createThought: async (content) => {
    const response = await apiRequest('/thoughts', {
      method: 'POST',
      body: { content },
    });
    return response;
  },

  deleteThought: async (id) => {
    await apiRequest(`/thoughts/${id}`, {
      method: 'DELETE',
    });
    return id; // Return the deleted thought id
  },
};
