import React, { useState, useEffect } from 'react';
import { authAPI, thoughtsAPI } from '../services/api';
import { 
  AppBar,
  Toolbar,
  IconButton,
  Typography, 
  Box, 
  TextField, 
  Paper,
  Container,
  Avatar,
  Divider
} from '@mui/material';
import { Logout as LogoutIcon } from '@mui/icons-material';
import SendIcon from '@mui/icons-material/Send';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const navigate = useNavigate();
  const [thought, setThought] = useState('');
  const [thoughts, setThoughts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch thoughts on component mount
  useEffect(() => {
    const fetchThoughts = async () => {
      try {
        setIsLoading(true);
        const data = await thoughtsAPI.getThoughts();
        setThoughts(data);
      } catch (err) {
        console.error('Failed to fetch thoughts:', err);
        setError('Failed to load thoughts');
      } finally {
        setIsLoading(false);
      }
    };

    fetchThoughts();
  }, []);

  const handleLogout = () => {
    authAPI.logout();
    navigate('/login');
  };

  const handleShareThought = async (e) => {
    e.preventDefault();
    if (!thought.trim()) return;

    try {
      setIsLoading(true);
      const newThought = await thoughtsAPI.createThought(thought);
      setThoughts([newThought, ...thoughts]);
      setThought('');
    } catch (err) {
      console.error('Failed to create thought:', err);
      setError('Failed to post thought');
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteThought = async (id) => {
    try {
      await thoughtsAPI.deleteThought(id);
      setThoughts(thoughts.filter(t => t.id !== id));
    } catch (err) {
      console.error('Failed to delete thought:', err);
      setError('Failed to delete thought');
    }
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh', bgcolor: '#f5f5f5' }}>
      <AppBar position="static" color="transparent" elevation={0}>
        <Container maxWidth="md">
          <Toolbar disableGutters sx={{ justifyContent: 'space-between' }}>
            <Typography variant="h6" component="div" sx={{ fontWeight: 500, letterSpacing: -0.5 }}>
              thoughts
            </Typography>
            <IconButton
              size="medium"
              onClick={handleLogout}
              sx={{ color: 'text.secondary' }}
            >
              <LogoutIcon fontSize="small" />
            </IconButton>
          </Toolbar>
        </Container>
      </AppBar>
      <Divider />

        <Container maxWidth="md" sx={{ flex: 1, py: 3, px: { xs: 2, sm: 3 } }}>
          <Box sx={{ mb: 4, textAlign: 'center' }}>
            <Typography variant="h5" component="h1" gutterBottom sx={{ fontWeight: 500, mb: 1 }}>
              Welcome
            </Typography>
            <Typography variant="body1" color="text.secondary">
              Share your thoughts...
            </Typography>
          </Box>

          {/* Thoughts List */}
          <Box sx={{ maxWidth: 600, mx: 'auto' }}>
            {thoughts.map((item) => (
              <Paper 
                key={item.id} 
                elevation={0} 
                sx={{ 
                  p: 2, 
                  mb: 2, 
                  bgcolor: 'background.paper',
                  borderRadius: 2,
                  border: '1px solid',
                  borderColor: 'divider',
                  position: 'relative',
                  '&:hover': {
                    '& .delete-button': {
                      opacity: 1,
                    }
                  }
                }}
              >
                <Typography variant="body1">{item.text}</Typography>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mt: 1 }}>
                  <Typography variant="caption" color="text.secondary">
                    {new Date(item.created_at).toLocaleString()}
                  </Typography>
                  <IconButton 
                    size="small" 
                    className="delete-button"
                    onClick={() => handleDeleteThought(item.id)}
                    sx={{ 
                      opacity: 0, 
                      transition: 'opacity 0.2s',
                      '&:hover': {
                        color: 'error.main'
                      }
                    }}
                  >
                    <DeleteIcon fontSize="small" />
                  </IconButton>
                </Box>
              </Paper>
            ))}
          </Box>
        </Container>

        <Box sx={{ mt: 'auto', py: 3, px: { xs: 2, sm: 0 } }}>
          <Container maxWidth="sm">
            <Box component="form" onSubmit={handleShareThought} sx={{ display: 'flex', gap: 1, mt: 2, mb: 4 }}>
              <TextField
                fullWidth
                variant="outlined"
                placeholder="What's on your mind?"
                value={thought}
                onChange={(e) => setThought(e.target.value)}
                size="small"
                disabled={isLoading}
                sx={{ 
                  '& .MuiOutlinedInput-root': { 
                    borderRadius: 2,
                    bgcolor: 'background.paper'
                  }
                }}
              />
              <Button 
                type="submit" 
                variant="contained" 
                color="primary"
                disabled={isLoading || !thought.trim()}
                sx={{ borderRadius: 2, minWidth: 100 }}
              >
                {isLoading ? 'Sharing...' : 'Share'}
              </Button>
            </Box>
          </Container>
        </Box>
    </Box>
  );
};

export default Home;
