import React, { useState } from 'react';
import { 
  AppBar,
  Toolbar,
  IconButton,
  Typography, 
  Box, 
  TextField, 
  Paper,
  Container,
  Avatar,
  Divider
} from '@mui/material';
import { Logout as LogoutIcon } from '@mui/icons-material';
import SendIcon from '@mui/icons-material/Send';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const navigate = useNavigate();
  const [thought, setThought] = useState('');
  const [thoughts, setThoughts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch thoughts on component mount
  useEffect(() => {
    const fetchThoughts = async () => {
      try {
        setIsLoading(true);
        const data = await thoughtsAPI.getThoughts();
        setThoughts(data);
      } catch (err) {
        console.error('Failed to fetch thoughts:', err);
        setError('Failed to load thoughts');
      } finally {
        setIsLoading(false);
      }
    };

    fetchThoughts();
  }, []);

  const handleLogout = () => {
    authAPI.logout();
    navigate('/login');
  };

  const handleShareThought = async (e) => {
    e.preventDefault();
    if (!thought.trim()) return;

    try {
      setIsLoading(true);
      const newThought = await thoughtsAPI.createThought(thought);
      setThoughts([newThought, ...thoughts]);
      setThought('');
    } catch (err) {
      console.error('Failed to create thought:', err);
      setError('Failed to post thought');
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteThought = async (id) => {
    try {
      await thoughtsAPI.deleteThought(id);
      setThoughts(thoughts.filter(t => t.id !== id));
    } catch (err) {
      console.error('Failed to delete thought:', err);
      setError('Failed to delete thought');
    }
  };

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh', bgcolor: '#f5f5f5' }}>
      <AppBar position="static" color="transparent" elevation={0}>
        <Container maxWidth="md">
          <Toolbar disableGutters sx={{ justifyContent: 'space-between' }}>
            <Typography variant="h6" component="div" sx={{ fontWeight: 500, letterSpacing: -0.5 }}>
              thoughts
            </Typography>
            <IconButton
              size="medium"
              onClick={handleLogout}
              sx={{ color: 'text.secondary' }}
            >
              <LogoutIcon fontSize="small" />
            </IconButton>
          </Toolbar>
        </Container>
      </AppBar>
      <Divider />

        <Container maxWidth="md" sx={{ flex: 1, py: 3, px: { xs: 2, sm: 3 } }}>
          <Box sx={{ mb: 4, textAlign: 'center' }}>
            <Typography variant="h5" component="h1" gutterBottom sx={{ fontWeight: 500, mb: 1 }}>
              Welcome
            </Typography>
            <Typography variant="body1" color="text.secondary">
              Share your thoughts...
            </Typography>
          </Box>

          {/* Thoughts List */}
          <Box sx={{ maxWidth: 600, mx: 'auto' }}>
            {thoughts.map((item) => (
              <Box 
                key={item.id}
                sx={{ 
                  p: 2, 
                  mb: 2,
                  bgcolor: 'background.paper',
                  borderRadius: 1,
                  border: '1px solid',
                  borderColor: 'divider',
                  '&:hover': {
                    borderColor: 'text.secondary'
                  }
                }}
              >
                <Typography variant="body1">{item.text}</Typography>
              </Box>
            ))}
          </Box>
        </Container>

        <Box sx={{ mt: 'auto', py: 3, px: { xs: 2, sm: 0 } }}>
          <Container maxWidth="sm">
            <Box 
              component="form"
              onSubmit={handleShareThought}
              sx={{ 
                display: 'flex',
                alignItems: 'center',
                gap: 1,
                bgcolor: 'background.paper',
                borderRadius: 2,
                px: 2,
                py: 1,
                border: '1px solid',
                borderColor: 'divider',
                '&:focus-within': {
                  borderColor: 'primary.main',
                  boxShadow: '0 0 0 1px primary.main'
                }
              }}
            >
              <TextField
                fullWidth
                variant="standard"
                placeholder="Share a thought..."
                value={thought}
                onChange={(e) => setThought(e.target.value)}
                InputProps={{
                  disableUnderline: true,
                  sx: {
                    '& input': {
                      py: 1.5,
                      '&::placeholder': {
                        opacity: 0.7
                      }
                    }
                  }
                }}
              />
              <IconButton 
                type="submit" 
                color="primary"
                disabled={!thought.trim()}
                sx={{ opacity: thought.trim() ? 1 : 0.5 }}
              >
                <SendIcon fontSize="small" />
              </IconButton>
            </Box>
          </Container>
        </Box>
    </Box>
  );
};

export default Home;
