const API_BASE_URL = 'http://localhost:9000/api'; // Include /api in the base URL

// Helper function to handle API requests
const apiRequest = async (endpoint, options = {}) => {
  const token = localStorage.getItem('token');

  const headers = {
    'Content-Type': 'application/json',
    ...options.headers,
  };

  if (token) {
    headers['Authorization'] = `Bearer ${token}`;
    console.log('Using token:', token);
  } else {
    console.log('No token found in localStorage');
  }

  try {
    console.log(`Making ${options.method || 'GET'} request to ${API_BASE_URL}${endpoint}`);
    console.log('Request headers:', headers);
    if (options.body) {
      console.log('Request body:', options.body);
    }

    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      ...options,
      headers,
      body: options.body ? JSON.stringify(options.body) : undefined,
    });

    const data = await response.json();
    console.log(`Response from ${endpoint}:`, { status: response.status, data });

    if (!response.ok) {
      // If token is invalid or expired, clear it and redirect to login
      if (response.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        // Redirect to login page if we're not already there
        if (!window.location.pathname.includes('/login')) {
          window.location.href = '/login';
        }
      }
      throw new Error(data.error || `Request failed with status ${response.status}`);
    }

    return data;
  } catch (error) {
    console.error('API Error:', error);
    throw error;
  }
};

// Auth API
export const authAPI = {
  login: async (email, password) => {
    const response = await apiRequest('/auth/login', {
      method: 'POST',
      body: { email, password },
    });
    // The backend returns the token directly as a string
    // Make sure we're not trying to access .token if it's already the token
    const token = typeof response === 'string' ? response : response.token;
    return {
      token,
      user: { email }
    };
  },

  register: async (email, password) => {
    const response = await apiRequest('/auth/register', {
      method: 'POST',
      body: { email, password },
    });
    // The backend returns the token directly as a string
    const token = typeof response === 'string' ? response : response.token;
    return {
      token,
      user: { email }
    };
  },

  getCurrentUser: async () => {
    try {
      const user = await apiRequest('/me');
      // Update localStorage with fresh user data
      localStorage.setItem('user', JSON.stringify(user));
      return user;
    } catch (error) {
      console.error('Failed to fetch user profile:', error);
      // Fallback to localStorage if API fails
      const user = localStorage.getItem('user');
      return user ? JSON.parse(user) : null;
    }
  },

  logout: () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
  },
};

// Thoughts API
export const thoughtsAPI = {
  getThoughts: async () => {
    const response = await apiRequest('/thoughts');
    // The backend returns thoughts in the data field
    return Array.isArray(response) ? response : [];
  },

  createThought: async (content) => {
    const response = await apiRequest('/thoughts', {
      method: 'POST',
      body: { content },
    });
    return response;
  },

  deleteThought: async (id) => {
    await apiRequest(`/thoughts/${id}`, {
      method: 'DELETE',
    });
    return id; // Return the deleted thought id
  },
};
